"use client"
import React from 'react';
import { useSetAtom } from 'jotai';
import { a as setPageAtom } from '../../../state.js';

function SlidesActionButton(props) {
    const { variant, children, onClick, ...rest } = props;
    const setPage = useSetAtom(setPageAtom);
    const action = () => {
        setPage({
            forward: variant === "right",
        });
    };
    return (React.createElement("div", { onClick: (e) => {
            action();
            onClick && onClick(e);
        }, ...rest }, children));
}

export { SlidesActionButton };
